import React, {useState} from 'react'

const Home = () => {
    
    let inputValue = 0;

    const buttonStyling = {
        padding: 20,
        border: "none",
        backgroundColor: "#f1f1f1",
        margin: 10,
    }

    const decrement = () => {
        inputValue --;
    }

    const increment = () => {
        inputValue ++;
    }
    
    return (<div>
    
    <input style = {{padding:20, border: "1px solid rgba(0,0,0,0.4)"}}
    type = "number" 
    placeholder = "Enter Sum"
    
    onChange={inputValue}
    readOnly
    />

    <button style={buttonStyling} onClick={decrement}>
        -
    </button>
    <button style={buttonStyling} onClick={increment}>
        +
    </button>

    </div>
  )

};

export default Home
