import React, {useState} from 'react'

const Home = () => {

    
    const [inputValue, setInputValue] = useState(0);

    const buttonStyling = {
        padding: 20,
        border: "none",
        backgroundColor: "#f1f1f1",
        margin: 10,
    }

    const decrement = () => {
        setInputValue(inputValue - 1);
    }

    const increment = () => {
        setInputValue(inputValue + 1);
    }
    
    return (<div>
    
    <input style = {{padding:20, border: "1px solid rgba(0,0,0,0.4)"}}
    type = "number" 
    placeholder = "Enter Sum"
    
    value={inputValue}
    readOnly
    
    />

    <button style={buttonStyling} onClick={decrement}>
        -
    </button>
    <button style={buttonStyling} onClick={increment}>
        +
    </button>

    </div>
  )

};

export default Home
